  PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=13 ;

--
-- Дамп данных таблицы `automat`
--

INSERT INTO `automat` (`id`, `name`, `calibr`, `weight`, `length`, `rapidity`, `capacity`, `effect`, `description`, `photo`) VALUES
(10, 'AK-74', '5.45x39мм', '3.07 кг', '415 мм', '600- 650 выстрелов в минуту', '30 патронов', '1000 м', 'Начиная со Второй Мировой войны в армиях мира начинают появляться боеприпасы уменьшенной мощности (промежуточные), имеющие, однако, стандартный винтовочный калибр 7.62 - 8 мм (немецкие 7.92х33мм, советский 7.62х39мм). Всерьез проблемой снижения калибра своих винтовок первыми занялись американцы, приняв на вооружение в середине 1960х штурмовую винтовку M16A1. Как только практический опыт американцев подтвердил возможность и полезность дальнейшего снижения калибров (теоретически эту необходимость до того обосновывали неоднократно), полномасштабные работы в этом направлении начались и в других странах, в том числе и в СССР. С начала 1960х годов в СССР на основе штатного патрона 7.62х39мм разрабатывались патроны калибра 5.6мм, а к концу шестидесятых уже был разработан новый патрон калибра 5.45мм, имевший удлиненную пулю с комбинированным стальным и свинцовым сердечником и полостью в носике. Начальная скорость пули составляла порядка 900 м/с, общая масса патрона 10,2 грамма, на 6 грамм меньше, чем масса патрона 7.62х39мм (16.2 г), что при носимом боекомплекте всего в 8 магазинов (240 патронов) дает экономию массы в 1,4 кг. Новый патрон также имел существенно более настильную траекторию пули, что обеспечивало большую почти на 100 метров дальность прямого выстрела. За счет особенностей конструкции пули при попадании в тело она должна была начинать кувыркаться, нанося более тяжелые, чем обычно, раны, однако по некоторым данным, так происходит не всегда.', 'ak74m_91.jpg'),
(11, 'Гроза" ОЦ-14', '9x39мм (СП-6, ПАБ-9), 7.62x39мм', '3,200 г', '415 мм', '750 выстрелов в минуту', 'патронов: 20 (9х39мм), 30 (7.62х39мм)', '700 м', 'Автоматно-гранатометный комплекс "Гроза" ОЦ-14 был разработан в Туле, в ЦКИБ СОО, и производился малыми партиями на Тульском оружейном заводе в середине 1990х годов. Индекс «ОЦ» обозначает «Образец ЦКИБ», такой индекс получают все модели боевого стрелкового оружия, созданные в ЦКИБ СОО (модели спортивного и охотничьего оружия получают индекс «МЦ»). Разработка автоматно-гранатометного комплекса для ближнего боя была начата в 1992 году конструкторами Валерием Телешем (разработчиком 40мм подствольных гранатометов ГП-25 и ГП-30) и Юрием Лебедевым, и уже в 1994 году были готовы первые прототипы. Основной идеей создания специализированного комплекса стало то, что традиционная установка подствольного гранатомета на штатный автомат (будь то АК-74 или М16А2) сильно ухудшает баланс оружия, а потому необходимо изначально проектировать оружие с учетом установки на него гранатомета. Кроме того, за счет модульной конструкции оружия предполагалось достичь большой гибкости в его применении.\r\nИзначально этот автоматно-гранатометный комплекс создавался для спецназа МВД под специальные 9мм патроны СП-5 и СП-6. Вариант "Гроза-1" (другое обозначение – ТКБ-0239) был создан для армейского спецназа под широко распространенный патрон 7,62х39. ', 'ots14_02.jpg'),
(12, 'FN Herstal F2000', '5.56x45 мм NATO', '3.6 кг без патронов в стандартной конфигурации; 4.6 кг с 40мм гранатометом', '400 мм', '850 выстрелов в минуту', '30 патронов', '500 м', 'Модульная система F2000 разработана фирмой Fabrique Nationale Herstal и впервые представлена публике в 2001 году, а начало разработки датируется первой половиной 1990х годов. Основными особенностями F2000 являются, помимо ставших уже традиционными схемы буллпап и модульной конструкции, еще и полная "двусторонность" оружия, достигаемая не только расположением органов управления так, что их можно достать любой рукой, но и выбросом стреляных гильз не вбок, а вперед, что позволяет стрелять из винтовки с любого плеча без необходимости в каких либо изменениях в механизме оружия.\r\nВ настоящее время (начало 2007 года) оружие серии F2000 интенсивно продвигается на мировом рынке оружия,эти винтовки уже состоят на вооруженииармии Словакии и сил специальных операций Бельгии.', 'fn_f2000_3.jpg');

