--
-- Дамп данных таблицы `shotgun`
--

INSERT INTO `shotgun` (`id`, `name`, `calibr`, `weight`, `length`, `rapidity`, `capacity`, `effect`, `description`, `photo`) VALUES
(3, 'Вепрь-12 ', '12 калибр, патронник 76 мм', '3,9 кг', '430 мм', '?', '8 патронов отъемный коробчатый', '?', 'Гладкоствольное ружье (карабин) Вепрь-12 являтеся относительно новой разработкой завода "Молот" (Вятские Поляны) и создано в качестве прямого конкурента пользующимся большой популярностью в России ружьям серии Сайга 12С / Сайга 12К.Основное назначение новых ружей - это спорт (практическая стрельба по правилам IPSC), а также защита дома и охранная деятельность. Кроме того, Вепрь-12 является хорошим оружием поддержки для милиции и иных силовых ведомств.\r\n В основу ружья Вепрь-12 положена проверенная временем конструкция ручного пулемета Калашникова РПК (также выпускаемого на заводе Молот), однако при его создании учтены пожелания стрелков-спортсменов, и в конструкцию ружья введены дополнительные элементы, делающие обращение с ним более удобным - двусторонний предохранитель, шахта магазина, затворная задержка и т.п. В настоящее время ружья серии Вепрь-12 выпускаются в трех исполнениях,отличающихся длинной ствола - в базовом варианте ствол самый короткий,в вариантах исполнение 01 и исполнение 02 стволы более длинные.', 'vepr12_1.jpg'),
(4, 'Форт-500', '12 (длина патронника 76 мм)', '3.8 кг', '510 мм', '?', '4 или 6 патронов в трубчатом подствольном магазине', '?', 'Гладкоствольное ружье Форт-500 было разработано в НПО "Форт" МВД Украины в конце 1990х годов, и предназначалось для вооружения сотрудников МВД и служб безопасности Украины. В настоящее время ружье ФОРТ-500 в ограниченных количествах состоит на вооружении спецподразделений МВД Украины, а также продается гражданам этой страны в охотничьем варианте. В конструкции этого ружья по имеющейся информации использованы некоторые решения, заимствованные из американского ружья Remington 870. В настоящее время наряду с базовым вариантом ружья Форт-500 выпускается и специальный "полицейский" вариант Форт-500М, имеющий укороченный до 355мм ствол, телескопический регулируемый приклад и комплект направляющих типа Picatinny rail для установки дополнительных аксессуаров.', 'fort-500m.jpg'),
(5, 'USAS-12', '12 (длина патронника 70 мм)', '5.5 кг без магазина, 6.2 кг с магазином и 10 патронами', '460 мм', '360 выстрелов в минуту в автоматическом режиме', 'рожковые на 10 патронов или барабанные на 20 патронов', '?', 'Гладкоствольное боевое ружье USAS-12 разработано в США в конце 1980х годов конструктором Джоном Тревором, работавшим на небольшую компанию Gilbert Equipment Co. В основу нового автоматического гладкоствольного ружья легли идеи американца Максвелла Атчиссона, реализованные им в экспериментальных ружьях серии AA-12. Так как компания Gilbert Equipment Co не имела собственных производственных мощностей, она начала поиск производителя, способного наладить выпуск нового оружия. В результате долгих поисков в качестве производителя нового ружья, получившего обозначение USAS-12, была выбрана Южно-корейская компания Daewoo Precision Industries. После незначительной доработки конструкции инженерами Daewoo ружье USAS-12 пошлов серийное производство. По имеющимся данным, к середине 1990х было выпущено более 30 тысяч этих ружей, разошедшихся главным образом по военным и полицейским структурам стран Азиатско-Тихоокеанского региона. Попытка компании Gilbert Equipment Co импортировать в США самозарядные варианты ружей USAS-12 для гражданского рынка окончились провалом - BATF (Бюро по контролюза алкоголем, табаком и огнестрельным оружием правительства США) наложило запретна продажу этих ружей на гражданском рынке. Для продажи этих ружей правительственным и полицейским структурам США в конце 1990х годов компания RAMO Defence наладила сборку ружей USAS-12 в США из деталей и компонентов, импортированных из Южной Кореи.', 'usas-12.jpg');

