--
-- Дамп данных таблицы `submachine_guns`
--

INSERT INTO `submachine_guns` (`id`, `name`, `calibr`, `weight`, `length`, `rapidity`, `capacity`, `effect`, `description`, `photo`) VALUES
(8, 'Lanchester Mk.1', '9x19mm Luger / Parabellum', '4,3 кг без патронов', '200 мм', '600 выстрелов в минуту', '50 патронов', '150-200 метров', 'Пистолет-пулемет Lanchester Mk.1 построен на основе автоматики со свободным затвором. Стрельба ведется с открытого затвора, режимы огня - автоматический и одиночные выстрелы; переключатель режимов огня расположен перед спусковым крючком. Предохранитель выполнен в виде паза в ствольной коробке, в который после отведения назад заводится для фиксации рукоятка затвора. Питание патронами осуществляется из примыкаемых слева коробчатых магазинов, выброс стреляных гильз - вправо. Приемник магазинов выполнениз бронзы. Ствольная коробка и кожух ствола соединены с деревянной ложей шарнирно в ее передней части, так что при разборке оружие как бы переламывается пополам. Прицельные приспособления состоят из мушки и регулируемого по дальности целика сустановками от 100 до 600 ярдов. Ложа деревянная, по форме повторяет ложу винтовки Lee-Enfield. От этойже винтовки заимствованы крепления для штык-ножа образца 1907 года.', 'lanchester1.jpg'),
(9, 'ППШ-2', '7.62х25 ТТ', '3.4 кг без магазина', '300 мм', '550 выстрелов в минуту', '35 патронов', '600 выстрелов в минуту', 'Пистолет-пулемет ППШ-2 использует автоматику со свободным затвором. Огонь ведется с открытого затвора, только автоматическим огнем. Роль предохранителя выполняет откидная пылезащитная крышка паза для рукоятки заряжания, имеющая два выреза для фиксации рукоятки при закрытом положении крышки и переднем либо заднем положении затвора. Питание патронами – из отъемных двухрядных коробчатых магазинов. Приклад деревянный, отъемный (что рассматривалось военными как недостаток этой системы из-за возможности его утери в бою), при этом оружие могло стрелять и со снятым прикладом. Также известен вариант ППШ-2 с выдвижным металлическим прикладом. В конструкции ППШ-2 широко использована штамповка из стального листа, на дульной части ствола выполнен компенсатор подброса ствола.', '1389960687.jpg'),
(10, 'Thompson M1921 M1928 M1 M1A1', '.45ACP', '4.69 кг', '267 мм', '800 выстр/мин', '20 или 30 патронов коробчатые или барабанные на 50 и 100 патронов', '100-150 метров', 'Джон Томпсон (John T. Thompson) начал разработку пистолетов-пулеметов в 1916 году, основав компанию Auto-Ordnance. Он приобрел патент американца Джона Блиша (John Blish) на систему замедления отката затвора при помощи трения, которую затем использовал в своих конструкциях. Первые образцы своих пистолетов-пулеметов Томпсон представил в 1919 году, а серийное производство началось в 1921 году, на заводе компании Colt Firearms Manufacturing Co.\r\nИмеющие характерный вид за счет частично оребренного ствола и легкосъемных приклада и передней рукоятки или цевъя, пистолеты-пулеметы Томпсона или "Томми-ганы" (Tommy-gun, от Thompson submachine gun) стали одним из символов эпохи "Сухого закона" в США (1920е-30е годы). Томми-ганы использовались как гангстерами (Джон Диллинжер, "детское лицо" Нельсон и др.) так и полицией. В тридцатые годы пистолеты-пулеметы Томпсона начали в ограниченных количествах поступать на вооружение флота США; в военных действиях они впервые участвовали во время карательной экспедиции ВМФ США в Никарагуа в середине тридцатых годов. В дальнейшем Томпсоны широко использовались в ходе Второй Мировой войны как самими американцами, так и британцами. ', 'tommy_m1921.jpg');

-- --------------------------------------------------------

--
-- Структура таблицы `travmat`
--

CREATE TABLE IF NOT EXISTS `travmat` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` text NOT NULL,
  `calibr` text NOT NULL,
  `weight` text NOT NULL,
  `length` text NOT NULL,
  `rapidity` text NOT NULL,
  `capacity` text NOT NULL,
  `effect` text NOT NULL,
  `description` text NOT NULL,
